SELECT COALESCE("t3"."MOVIE_ID", "t3"."MOVIE_ID") AS "MOVIE_ID", "t3"."TITLE", "t3"."PRODUCTION_YEAR", "t3"."KEYWORDS", "t3"."COMPANY_TYPES", "t3"."COMPANIES", "t3"."CAST_MEMBERS", CASE WHEN "t5"."INFO_DETAILS" IS NOT NULL THEN CAST("t5"."INFO_DETAILS" AS VARCHAR CHARACTER SET "ISO-8859-1") ELSE 'No additional info' END AS "INFO_DETAILS"
FROM (SELECT ANY_VALUE("id") AS "MOVIE_ID", "TITLE", "PRODUCTION_YEAR", LISTAGG(DISTINCT "keyword", "FD_COL_4") AS "KEYWORDS", LISTAGG(DISTINCT "kind", "FD_COL_4") AS "COMPANY_TYPES", LISTAGG(DISTINCT "name", "FD_COL_4") AS "COMPANIES", LISTAGG(DISTINCT "name0", "FD_COL_4") AS "CAST_MEMBERS"
FROM "s1"
GROUP BY "id", "TITLE", "PRODUCTION_YEAR") AS "t3"
LEFT JOIN (SELECT "movie_info"."movie_id" AS "MOVIE_ID", LISTAGG(DISTINCT "info_type"."info", ', ') AS "INFO_DETAILS"
FROM "IMDB"."movie_info"
INNER JOIN "IMDB"."info_type" ON "movie_info"."info_type_id" = "info_type"."id"
GROUP BY "movie_info"."movie_id") AS "t5" ON "t3"."MOVIE_ID" = "t5"."MOVIE_ID"
ORDER BY "t3"."PRODUCTION_YEAR" DESC NULLS FIRST, "t3"."TITLE"