SELECT COALESCE("ACTOR_NAME", "ACTOR_NAME") AS "ACTOR_NAME", "MOVIE_TITLE", "PRODUCTION_YEAR", "CAST_TYPE", "KEYWORDS", "production_year" AS "production_year_"
FROM (SELECT ANY_VALUE("name") AS "ACTOR_NAME", ANY_VALUE("title") AS "MOVIE_TITLE", "PRODUCTION_YEAR", ANY_VALUE("kind") AS "CAST_TYPE", LISTAGG(DISTINCT "keyword", "FD_COL_5") AS "KEYWORDS", "PRODUCTION_YEAR" AS "production_year"
FROM "s1"
GROUP BY "name", "title", "PRODUCTION_YEAR", "kind"
ORDER BY "PRODUCTION_YEAR" DESC NULLS FIRST, 1) AS "t4"