SELECT COALESCE("t3"."MOVIE_TITLE", "t3"."MOVIE_TITLE") AS "MOVIE_TITLE", "t3"."PERSON_INFO", "t3"."ACTOR_NAME", "t3"."MOVIE_KEYWORD", "t3"."COMPANY_NAME", "t3"."COMPANY_TYPE", "t3"."production_year"
FROM (SELECT "t1"."title" AS "MOVIE_TITLE", "t1"."info" AS "PERSON_INFO", "t1"."name" AS "ACTOR_NAME", "keyword"."keyword" AS "MOVIE_KEYWORD", "company_name"."name" AS "COMPANY_NAME", "company_type"."kind" AS "COMPANY_TYPE", "t1"."production_year"
FROM "IMDB"."company_type"
INNER JOIN ("IMDB"."company_name" INNER JOIN "IMDB"."movie_companies" ON "company_name"."id" = "movie_companies"."company_id") ON "company_type"."id" = "movie_companies"."company_type_id"
INNER JOIN ((SELECT "s1"."id", "s1"."title", "s1"."imdb_index", "s1"."kind_id", "s1"."production_year", "s1"."imdb_id", "s1"."phonetic_code", "s1"."episode_of_id", "s1"."season_nr", "s1"."episode_nr", "s1"."series_years", "s1"."md5sum", "complete_cast"."id" AS "id0", "complete_cast"."movie_id", "complete_cast"."subject_id", "complete_cast"."status_id", "aka_name"."id" AS "id1", "aka_name"."person_id", "aka_name"."name", "aka_name"."imdb_index" AS "imdb_index0", "aka_name"."name_pcode_cf", "aka_name"."name_pcode_nf", "aka_name"."surname_pcode", "aka_name"."md5sum" AS "md5sum0", "person_info"."id" AS "id2", "person_info"."person_id" AS "person_id0", "person_info"."info_type_id", "person_info"."info", "person_info"."note", "movie_keyword"."id" AS "id3", "movie_keyword"."movie_id" AS "movie_id0", "movie_keyword"."keyword_id"
FROM "IMDB"."movie_keyword"
RIGHT JOIN ("IMDB"."person_info" INNER JOIN ("IMDB"."aka_name" INNER JOIN ("s1" INNER JOIN "IMDB"."complete_cast" ON "s1"."id" = "complete_cast"."movie_id") ON "aka_name"."person_id" = "complete_cast"."subject_id") ON "person_info"."person_id" = "aka_name"."person_id") ON "movie_keyword"."movie_id" = "s1"."id") AS "t1" LEFT JOIN "IMDB"."keyword" ON "t1"."keyword_id" = "keyword"."id") ON "movie_companies"."movie_id" = "t1"."id"
ORDER BY "t1"."production_year" DESC NULLS FIRST, "t1"."title") AS "t3"