SELECT COALESCE("TITLE_ID", "TITLE_ID") AS "TITLE_ID", "TITLE", "PRODUCTION_YEAR", "ACTOR_COUNT", "ACTOR_NAMES", "KEYWORDS"
FROM (SELECT "TITLE_ID", "TITLE", "PRODUCTION_YEAR", CAST(CASE WHEN "ACTOR_COUNT" IS NOT NULL THEN CAST("ACTOR_COUNT" AS BIGINT) ELSE 0 END AS INTEGER) AS "ACTOR_COUNT", CASE WHEN "ACTOR_NAMES" IS NOT NULL THEN CAST("ACTOR_NAMES" AS VARCHAR CHARACTER SET "ISO-8859-1") ELSE 'None' END AS "ACTOR_NAMES", CASE WHEN "KEYWORDS" IS NOT NULL THEN CAST("KEYWORDS" AS VARCHAR CHARACTER SET "ISO-8859-1") ELSE 'None' END AS "KEYWORDS"
FROM "s1"
ORDER BY "PRODUCTION_YEAR" DESC NULLS FIRST, "TITLE") AS "t7"