SELECT COALESCE("MOVIE_ID", "MOVIE_ID") AS "MOVIE_ID", "TITLE", "PRODUCTION_YEAR", "AKA_NAMES", "KEYWORDS", "COMPANIES", "CAST_DETAILS"
FROM (SELECT ANY_VALUE("id") AS "MOVIE_ID", "TITLE", "PRODUCTION_YEAR", LISTAGG(DISTINCT "name", "FD_COL_4") AS "AKA_NAMES", LISTAGG(DISTINCT "keyword", "FD_COL_4") AS "KEYWORDS", LISTAGG(DISTINCT "name0", "FD_COL_4") AS "COMPANIES", LISTAGG(DISTINCT "FD_COL_7", "FD_COL_4") AS "CAST_DETAILS"
FROM "s1"
GROUP BY "id", "TITLE", "PRODUCTION_YEAR"
ORDER BY "PRODUCTION_YEAR" DESC NULLS FIRST) AS "t6"