SELECT COALESCE("t4"."ACTOR_NAME", "t4"."ACTOR_NAME") AS "ACTOR_NAME", "t4"."MOVIE_TITLE", "t4"."PRODUCTION_YEAR", "t4"."COMPANY_TYPE", "t4"."MOVIE_KEYWORD", "t4"."PERSON_INFO", "t4"."ROLE_DESCRIPTION"
FROM (SELECT "t2"."name" AS "ACTOR_NAME", "t2"."title" AS "MOVIE_TITLE", "t2"."production_year" AS "PRODUCTION_YEAR", "t2"."kind" AS "COMPANY_TYPE", "t2"."keyword" AS "MOVIE_KEYWORD", "t2"."info" AS "PERSON_INFO", "role_type"."role" AS "ROLE_DESCRIPTION"
FROM (SELECT "cast_info"."id", "cast_info"."person_id", "cast_info"."movie_id", "cast_info"."person_role_id", "cast_info"."note", "cast_info"."nr_order", "cast_info"."role_id", "aka_name"."id" AS "id0", "aka_name"."person_id" AS "person_id0", "aka_name"."name", "aka_name"."imdb_index", "aka_name"."name_pcode_cf", "aka_name"."name_pcode_nf", "aka_name"."surname_pcode", "aka_name"."md5sum", "t1"."id" AS "id1", "t1"."title", "t1"."imdb_index" AS "imdb_index0", "t1"."kind_id", "t1"."production_year", "t1"."imdb_id", "t1"."phonetic_code", "t1"."episode_of_id", "t1"."season_nr", "t1"."episode_nr", "t1"."series_years", "t1"."md5sum" AS "md5sum0", "s1"."id" AS "id2", "s1"."movie_id" AS "movie_id0", "s1"."company_id", "s1"."company_type_id", "s1"."note" AS "note0", "company_name"."id" AS "id3", "company_name"."name" AS "name0", "company_name"."country_code", "company_name"."imdb_id" AS "imdb_id0", "company_name"."name_pcode_nf" AS "name_pcode_nf0", "company_name"."name_pcode_sf", "company_name"."md5sum" AS "md5sum1", "s1"."id0" AS "id4", "s1"."kind", "movie_keyword"."id" AS "id5", "movie_keyword"."movie_id" AS "movie_id1", "movie_keyword"."keyword_id", "keyword"."id" AS "id6", "keyword"."keyword", "keyword"."phonetic_code" AS "phonetic_code0", "person_info"."id" AS "id7", "person_info"."person_id" AS "person_id1", "person_info"."info_type_id", "person_info"."info", "person_info"."note" AS "note1"
FROM "IMDB"."person_info"
RIGHT JOIN ("IMDB"."cast_info" INNER JOIN "IMDB"."aka_name" ON "cast_info"."person_id" = "aka_name"."person_id" INNER JOIN ("s1" INNER JOIN ("IMDB"."movie_keyword" INNER JOIN (SELECT *
FROM "IMDB"."title"
WHERE "production_year" >= 2000) AS "t1" ON "movie_keyword"."movie_id" = "t1"."id" INNER JOIN "IMDB"."keyword" ON "movie_keyword"."keyword_id" = "keyword"."id") ON "s1"."movie_id" = "t1"."id" INNER JOIN "IMDB"."company_name" ON "s1"."company_id" = "company_name"."id") ON "cast_info"."movie_id" = "t1"."id") ON "person_info"."person_id" = "aka_name"."id") AS "t2"
LEFT JOIN "IMDB"."role_type" ON "t2"."role_id" = "role_type"."id"
ORDER BY "t2"."production_year" DESC NULLS FIRST, "t2"."name") AS "t4"