SELECT COALESCE("t0"."name", "t0"."name") AS "name", "t0"."title", "t0"."production_year" AS "PRODUCTION_YEAR", "t0"."kind", ANY_VALUE("t0"."name") AS "ACTOR_NAME", ANY_VALUE("t0"."title") AS "MOVIE_TITLE", LISTAGG(DISTINCT "keyword"."keyword", ', ') AS "KEYWORDS", ANY_VALUE("t0"."kind") AS "COMPANY_TYPE"
FROM (SELECT "aka_name"."id", "aka_name"."person_id", "aka_name"."name", "aka_name"."imdb_index", "aka_name"."name_pcode_cf", "aka_name"."name_pcode_nf", "aka_name"."surname_pcode", "aka_name"."md5sum", "cast_info"."id" AS "id0", "cast_info"."person_id" AS "person_id0", "cast_info"."movie_id", "cast_info"."person_role_id", "cast_info"."note", "cast_info"."nr_order", "cast_info"."role_id", "t"."id" AS "id1", "t"."movie_id" AS "movie_id0", "t"."title", "t"."imdb_index" AS "imdb_index0", "t"."kind_id", "t"."production_year", "t"."phonetic_code", "t"."episode_of_id", "t"."season_nr", "t"."episode_nr", "t"."note" AS "note0", "t"."md5sum" AS "md5sum0", "movie_companies"."id" AS "id2", "movie_companies"."movie_id" AS "movie_id1", "movie_companies"."company_id", "movie_companies"."company_type_id", "movie_companies"."note" AS "note1", "company_type"."id" AS "id3", "company_type"."kind", "movie_keyword"."id" AS "id4", "movie_keyword"."movie_id" AS "movie_id2", "movie_keyword"."keyword_id"
FROM "IMDB"."movie_keyword"
RIGHT JOIN ("IMDB"."cast_info" INNER JOIN ("IMDB"."movie_companies" INNER JOIN (SELECT *
FROM "IMDB"."aka_title"
WHERE "production_year" >= 2000) AS "t" ON "movie_companies"."movie_id" = "t"."id" INNER JOIN "IMDB"."company_type" ON "movie_companies"."company_type_id" = "company_type"."id") ON "cast_info"."movie_id" = "t"."movie_id" INNER JOIN "IMDB"."aka_name" ON "cast_info"."person_id" = "aka_name"."person_id") ON "movie_keyword"."movie_id" = "t"."id") AS "t0"
LEFT JOIN "IMDB"."keyword" ON "t0"."keyword_id" = "keyword"."id"
GROUP BY "t0"."name", "t0"."title", "t0"."production_year", "t0"."kind"