SELECT COALESCE("company_name"."id", "company_name"."id") AS "id", "company_name"."name", "company_name"."country_code", "company_name"."imdb_id", "company_name"."name_pcode_nf", "company_name"."name_pcode_sf", "company_name"."md5sum", "t"."id" AS "id0", "t"."kind", "cast_info"."id" AS "id00", "cast_info"."person_id", "cast_info"."movie_id", "cast_info"."person_role_id", "cast_info"."note", "cast_info"."nr_order", "cast_info"."role_id", "aka_name"."id" AS "id000", "aka_name"."person_id" AS "person_id0", "aka_name"."name" AS "name0", "aka_name"."imdb_index", "aka_name"."name_pcode_cf", "aka_name"."name_pcode_nf" AS "name_pcode_nf0", "aka_name"."surname_pcode", "aka_name"."md5sum" AS "md5sum0", "person_info"."id" AS "id0000", "person_info"."person_id" AS "person_id00", "person_info"."info_type_id", "person_info"."info", "person_info"."note" AS "note0", "t0"."id" AS "id1", "t0"."keyword", "t0"."phonetic_code", "movie_keyword"."id" AS "id01", "movie_keyword"."movie_id" AS "movie_id0", "movie_keyword"."keyword_id", "t1"."id" AS "id10", "t1"."title", "t1"."imdb_index" AS "imdb_index0", "t1"."kind_id", "t1"."production_year", "t1"."imdb_id" AS "imdb_id0", "t1"."phonetic_code" AS "phonetic_code0", "t1"."episode_of_id", "t1"."season_nr", "t1"."episode_nr", "t1"."series_years", "t1"."md5sum" AS "md5sum00", "movie_companies"."id" AS "id00000", "movie_companies"."movie_id" AS "movie_id00", "movie_companies"."company_id", "movie_companies"."company_type_id", "movie_companies"."note" AS "note1"
FROM "IMDB"."company_name"
INNER JOIN ((SELECT *
FROM "IMDB"."company_type"
WHERE "kind" = 'Production') AS "t" INNER JOIN ("IMDB"."cast_info" INNER JOIN ("IMDB"."aka_name" INNER JOIN "IMDB"."person_info" ON "aka_name"."person_id" = "person_info"."person_id") ON "cast_info"."person_id" = "aka_name"."person_id" INNER JOIN ((SELECT *
FROM "IMDB"."keyword"
WHERE "keyword" LIKE '%drama%') AS "t0" INNER JOIN "IMDB"."movie_keyword" ON "t0"."id" = "movie_keyword"."keyword_id" INNER JOIN ((SELECT *
FROM "IMDB"."title"
WHERE "production_year" > 2000) AS "t1" INNER JOIN "IMDB"."movie_companies" ON "t1"."id" = "movie_companies"."movie_id") ON "movie_keyword"."movie_id" = "t1"."id") ON "cast_info"."movie_id" = "t1"."id") ON "t"."id" = "movie_companies"."company_type_id") ON "company_name"."id" = "movie_companies"."company_id"