SELECT COALESCE("t4"."ACTOR_NAME", "t4"."ACTOR_NAME") AS "ACTOR_NAME", "t4"."MOVIE_TITLE", "t4"."COMPANY_TYPE", "t4"."KEYWORDS", "t4"."INFO_COUNT"
FROM (SELECT "t1"."name", "t1"."title", "t1"."kind", ANY_VALUE("t1"."name") AS "ACTOR_NAME", ANY_VALUE("t1"."title") AS "MOVIE_TITLE", ANY_VALUE("t1"."kind") AS "COMPANY_TYPE", LISTAGG(DISTINCT "keyword"."keyword", ',') AS "KEYWORDS", COUNT(DISTINCT "movie_info"."id") AS "INFO_COUNT"
FROM (SELECT "cast_info"."id", "cast_info"."person_id", "cast_info"."movie_id", "cast_info"."person_role_id", "cast_info"."note", "cast_info"."nr_order", "cast_info"."role_id", "aka_name"."id" AS "id0", "aka_name"."person_id" AS "person_id0", "aka_name"."name", "aka_name"."imdb_index", "aka_name"."name_pcode_cf", "aka_name"."name_pcode_nf", "aka_name"."surname_pcode", "aka_name"."md5sum", "s1"."id0" AS "id1", "s1"."movie_id0", "s1"."title", "s1"."imdb_index" AS "imdb_index0", "s1"."kind_id", "s1"."production_year", "s1"."phonetic_code", "s1"."episode_of_id", "s1"."season_nr", "s1"."episode_nr", "s1"."note0", "s1"."md5sum" AS "md5sum0", "s1"."id" AS "id2", "s1"."movie_id" AS "movie_id1", "s1"."company_id", "s1"."company_type_id", "s1"."note" AS "note1", "company_type"."id" AS "id3", "company_type"."kind", "movie_keyword"."id" AS "id4", "movie_keyword"."movie_id" AS "movie_id2", "movie_keyword"."keyword_id"
FROM "IMDB"."movie_keyword"
RIGHT JOIN ("IMDB"."cast_info" INNER JOIN ("s1" INNER JOIN "IMDB"."company_type" ON "s1"."company_type_id" = "company_type"."id") ON "cast_info"."movie_id" = "s1"."movie_id0" INNER JOIN "IMDB"."aka_name" ON "cast_info"."person_id" = "aka_name"."person_id") ON "movie_keyword"."movie_id" = "s1"."id0") AS "t1"
LEFT JOIN "IMDB"."keyword" ON "t1"."keyword_id" = "keyword"."id"
LEFT JOIN "IMDB"."movie_info" ON "t1"."id1" = "movie_info"."movie_id"
GROUP BY "t1"."name", "t1"."title", "t1"."kind"
ORDER BY 8 DESC NULLS FIRST, 4) AS "t4"