SELECT COALESCE("title"."title", "title"."title") AS "MOVIE_TITLE", "title"."production_year" AS "PRODUCTION_YEAR", "company_name"."name" AS "COMPANY_NAME", ', ' AS "FD_COL_3", "keyword"."keyword" AS "MOVIE_KEYWORD", "aka_name"."name" || ' (' || "role_type"."role" || ')' AS "FD_COL_5"
FROM "IMDB"."company_name"
INNER JOIN ("IMDB"."role_type" INNER JOIN ("IMDB"."complete_cast" INNER JOIN ("IMDB"."aka_name" INNER JOIN "IMDB"."cast_info" ON "aka_name"."person_id" = "cast_info"."person_id") ON "complete_cast"."subject_id" = "cast_info"."person_id") ON "role_type"."id" = "cast_info"."role_id" INNER JOIN ("IMDB"."keyword" INNER JOIN "IMDB"."movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" INNER JOIN ("IMDB"."title" INNER JOIN "IMDB"."movie_companies" ON "title"."id" = "movie_companies"."movie_id") ON "movie_keyword"."movie_id" = "title"."id") ON "complete_cast"."movie_id" = "title"."id") ON "company_name"."id" = "movie_companies"."company_id"