SELECT COALESCE("t8"."PRODUCTION_YEAR", "t8"."PRODUCTION_YEAR") AS "PRODUCTION_YEAR", "t8"."TITLE_COUNT", "t8"."AVG_TITLE_LENGTH", "t8"."COMPANY_TYPE", "t8"."COMPANY_COUNT"
FROM (SELECT "s1"."PRODUCTION_YEAR", "s1"."TITLE_COUNT", "s1"."AVG_TITLE_LENGTH", ANY_VALUE("company_type"."kind") AS "COMPANY_TYPE", COUNT(DISTINCT "s1"."company_id") AS "COMPANY_COUNT"
FROM "IMDB"."company_type"
INNER JOIN "s1" ON "company_type"."id" = "s1"."company_type_id"
GROUP BY "company_type"."kind", "s1"."PRODUCTION_YEAR", "s1"."TITLE_COUNT", "s1"."AVG_TITLE_LENGTH"
ORDER BY "s1"."PRODUCTION_YEAR", "s1"."AVG_TITLE_LENGTH" DESC NULLS FIRST) AS "t8"