SELECT COALESCE("t9"."MOVIE_ID", "t9"."MOVIE_ID") AS "MOVIE_ID", "t9"."TITLE", "t9"."PRODUCTION_YEAR", "t9"."KEYWORDS", "t9"."COMPANY_TYPES", "t9"."COMPANIES", "t9"."CAST_MEMBERS", "t9"."INFO_DETAILS"
FROM (SELECT "t7"."MOVIE_ID", "t7"."TITLE", "t7"."PRODUCTION_YEAR", "t7"."KEYWORDS", "t7"."COMPANY_TYPES", "t7"."COMPANIES", "t7"."CAST_MEMBERS", CASE WHEN "t5"."INFO_DETAILS" IS NOT NULL THEN CAST("t5"."INFO_DETAILS" AS VARCHAR CHARACTER SET "ISO-8859-1") ELSE 'No additional info' END AS "INFO_DETAILS"
FROM (SELECT "movie_info"."movie_id" AS "MOVIE_ID", LISTAGG(DISTINCT "info_type"."info", ', ') AS "INFO_DETAILS"
FROM "IMDB"."info_type"
INNER JOIN "IMDB"."movie_info" ON "info_type"."id" = "movie_info"."info_type_id"
GROUP BY "movie_info"."movie_id") AS "t5"
RIGHT JOIN (SELECT ANY_VALUE("id") AS "MOVIE_ID", "TITLE", "PRODUCTION_YEAR", LISTAGG(DISTINCT "keyword", "FD_COL_4") AS "KEYWORDS", LISTAGG(DISTINCT "kind", "FD_COL_4") AS "COMPANY_TYPES", LISTAGG(DISTINCT "name", "FD_COL_4") AS "COMPANIES", LISTAGG(DISTINCT "name0", "FD_COL_4") AS "CAST_MEMBERS"
FROM "s1"
GROUP BY "id", "TITLE", "PRODUCTION_YEAR") AS "t7" ON "t5"."MOVIE_ID" = "t7"."MOVIE_ID"
ORDER BY "t7"."PRODUCTION_YEAR" DESC NULLS FIRST, "t7"."TITLE") AS "t9"