SELECT COALESCE("MOVIE_ID", "MOVIE_ID") AS "MOVIE_ID", "MOVIE_TITLE", "PRODUCTION_YEAR", "CAST_COUNT", "ACTOR_NAMES", "KEYWORDS", "COMPANIES"
FROM (SELECT "MOVIE_ID0" AS "MOVIE_ID", "MOVIE_TITLE", "PRODUCTION_YEAR", "CAST_COUNT", "ACTOR_NAMES", CASE WHEN "KEYWORDS" IS NOT NULL THEN CAST("KEYWORDS" AS VARCHAR CHARACTER SET "ISO-8859-1") ELSE 'No keywords' END AS "KEYWORDS", CASE WHEN "COMPANIES" IS NOT NULL THEN CAST("COMPANIES" AS VARCHAR CHARACTER SET "ISO-8859-1") ELSE 'No companies listed' END AS "COMPANIES"
FROM "s1"
ORDER BY "PRODUCTION_YEAR" DESC NULLS FIRST, "CAST_COUNT" DESC NULLS FIRST) AS "t10"