SELECT COALESCE("t4"."ACTOR_NAME", "t4"."ACTOR_NAME") AS "ACTOR_NAME", "t4"."MOVIE_TITLE", "t4"."PRODUCTION_YEAR", "t4"."CAST_TYPE", "t4"."KEYWORDS", "t4"."PRODUCTION_YEAR" AS "production_year_"
FROM (SELECT "t1"."name", "t1"."title", "t1"."production_year" AS "PRODUCTION_YEAR", "t1"."kind", ANY_VALUE("t1"."name") AS "ACTOR_NAME", ANY_VALUE("t1"."title") AS "MOVIE_TITLE", ANY_VALUE("t1"."kind") AS "CAST_TYPE", LISTAGG(DISTINCT "keyword"."keyword", ', ') AS "KEYWORDS"
FROM "IMDB"."keyword"
RIGHT JOIN (SELECT "aka_name"."id", "aka_name"."person_id", "aka_name"."name", "aka_name"."imdb_index", "aka_name"."name_pcode_cf", "aka_name"."name_pcode_nf", "aka_name"."surname_pcode", "aka_name"."md5sum", "cast_info"."id" AS "id0", "cast_info"."person_id" AS "person_id0", "cast_info"."movie_id", "cast_info"."person_role_id", "cast_info"."note", "cast_info"."nr_order", "cast_info"."role_id", "s1"."id" AS "id1", "s1"."title", "s1"."imdb_index" AS "imdb_index0", "s1"."kind_id", "s1"."production_year", "s1"."imdb_id", "s1"."phonetic_code", "s1"."episode_of_id", "s1"."season_nr", "s1"."episode_nr", "s1"."series_years", "s1"."md5sum" AS "md5sum0", "comp_cast_type"."id" AS "id2", "comp_cast_type"."kind", "movie_keyword"."id" AS "id3", "movie_keyword"."movie_id" AS "movie_id0", "movie_keyword"."keyword_id"
FROM "IMDB"."movie_keyword"
RIGHT JOIN ("IMDB"."aka_name" INNER JOIN ("s1" INNER JOIN ("IMDB"."cast_info" INNER JOIN "IMDB"."comp_cast_type" ON "cast_info"."person_role_id" = "comp_cast_type"."id") ON "s1"."id" = "cast_info"."movie_id") ON "aka_name"."person_id" = "cast_info"."person_id") ON "movie_keyword"."movie_id" = "s1"."id") AS "t1" ON "keyword"."id" = "t1"."keyword_id"
GROUP BY "t1"."name", "t1"."title", "t1"."production_year", "t1"."kind"
ORDER BY "t1"."production_year" DESC NULLS FIRST, 5) AS "t4"