SELECT COALESCE("t4"."MOVIE_ID", "t4"."MOVIE_ID") AS "MOVIE_ID", "t4"."TITLE", "t4"."PRODUCTION_YEAR", "t4"."COMPANY_NAME", "t4"."KEYWORDS", "t4"."ACTORS", COUNT(DISTINCT "cast_info"."id") AS "TOTAL_CAST_MEMBERS"
FROM "IMDB"."cast_info"
RIGHT JOIN (SELECT ANY_VALUE("t1"."id") AS "MOVIE_ID", "t1"."title" AS "TITLE", "t1"."production_year" AS "PRODUCTION_YEAR", ANY_VALUE("t1"."name") AS "COMPANY_NAME", LISTAGG(DISTINCT "t1"."keyword", ', ') AS "KEYWORDS", LISTAGG(DISTINCT "aka_name"."name", ', ') AS "ACTORS"
FROM (SELECT "t0"."id", "t0"."movie_id", "t0"."title", "t0"."imdb_index", "t0"."kind_id", "t0"."production_year", "t0"."phonetic_code", "t0"."episode_of_id", "t0"."season_nr", "t0"."episode_nr", "t0"."note", "t0"."md5sum", "t0"."id0", "t0"."movie_id0", "t0"."company_id", "t0"."company_type_id", "t0"."note0", "t0"."id1", "t0"."name", "t0"."country_code", "t0"."imdb_id", "t0"."name_pcode_nf", "t0"."name_pcode_sf", "t0"."md5sum0", "t0"."id2", "t0"."movie_id1", "t0"."keyword_id", "keyword"."id" AS "id3", "keyword"."keyword", "keyword"."phonetic_code" AS "phonetic_code0", "cast_info0"."id" AS "id4", "cast_info0"."person_id", "cast_info0"."movie_id" AS "movie_id2", "cast_info0"."person_role_id", "cast_info0"."note" AS "note1", "cast_info0"."nr_order", "cast_info0"."role_id"
FROM "IMDB"."cast_info" AS "cast_info0"
RIGHT JOIN ((SELECT "t"."id", "t"."movie_id", "t"."title", "t"."imdb_index", "t"."kind_id", "t"."production_year", "t"."phonetic_code", "t"."episode_of_id", "t"."season_nr", "t"."episode_nr", "t"."note", "t"."md5sum", "movie_companies"."id" AS "id0", "movie_companies"."movie_id" AS "movie_id0", "movie_companies"."company_id", "movie_companies"."company_type_id", "movie_companies"."note" AS "note0", "company_name"."id" AS "id1", "company_name"."name", "company_name"."country_code", "company_name"."imdb_id", "company_name"."name_pcode_nf", "company_name"."name_pcode_sf", "company_name"."md5sum" AS "md5sum0", "movie_keyword"."id" AS "id2", "movie_keyword"."movie_id" AS "movie_id1", "movie_keyword"."keyword_id"
FROM "IMDB"."movie_keyword"
RIGHT JOIN ("IMDB"."company_name" INNER JOIN ("IMDB"."movie_companies" INNER JOIN (SELECT *
FROM "IMDB"."aka_title"
WHERE "production_year" >= 2000) AS "t" ON "movie_companies"."movie_id" = "t"."id") ON "company_name"."id" = "movie_companies"."company_id") ON "movie_keyword"."movie_id" = "t"."id") AS "t0" LEFT JOIN "IMDB"."keyword" ON "t0"."keyword_id" = "keyword"."id") ON "cast_info0"."movie_id" = "t0"."id") AS "t1"
LEFT JOIN "IMDB"."aka_name" ON "t1"."person_id" = "aka_name"."person_id"
GROUP BY "t1"."id", "t1"."title", "t1"."production_year", "t1"."name") AS "t4" ON "cast_info"."movie_id" = "t4"."MOVIE_ID"
GROUP BY "t4"."MOVIE_ID", "t4"."TITLE", "t4"."PRODUCTION_YEAR", "t4"."COMPANY_NAME", "t4"."KEYWORDS", "t4"."ACTORS"