SELECT COALESCE("t4"."AKA_NAME", "t4"."AKA_NAME") AS "AKA_NAME", "t4"."MOVIE_TITLE", "t4"."CAST_ORDER", "t4"."PERSON_INFO", "t4"."COMPANY_NAME", "t4"."MOVIE_KEYWORD", "t4"."ACTOR_ROLE", "t4"."production_year"
FROM (SELECT "t2"."name" AS "AKA_NAME", "t2"."title" AS "MOVIE_TITLE", "t2"."nr_order" AS "CAST_ORDER", "t2"."info" AS "PERSON_INFO", "company_name"."name" AS "COMPANY_NAME", "keyword"."keyword" AS "MOVIE_KEYWORD", "t1"."role" AS "ACTOR_ROLE", "t2"."production_year"
FROM (SELECT *
FROM "IMDB"."role_type"
WHERE "role" = 'Actor') AS "t1"
INNER JOIN ((SELECT "aka_name"."id", "aka_name"."person_id", "aka_name"."name", "aka_name"."imdb_index", "aka_name"."name_pcode_cf", "aka_name"."name_pcode_nf", "aka_name"."surname_pcode", "aka_name"."md5sum", "cast_info"."id" AS "id0", "cast_info"."person_id" AS "person_id0", "cast_info"."movie_id", "cast_info"."person_role_id", "cast_info"."note", "cast_info"."nr_order", "cast_info"."role_id", "s1"."id" AS "id1", "s1"."title", "s1"."imdb_index" AS "imdb_index0", "s1"."kind_id", "s1"."production_year", "s1"."imdb_id", "s1"."phonetic_code", "s1"."episode_of_id", "s1"."season_nr", "s1"."episode_nr", "s1"."series_years", "s1"."md5sum" AS "md5sum0", "person_info"."id" AS "id2", "person_info"."person_id" AS "person_id1", "person_info"."info_type_id", "person_info"."info", "person_info"."note" AS "note0"
FROM "IMDB"."person_info"
RIGHT JOIN ("IMDB"."cast_info" INNER JOIN "s1" ON "cast_info"."movie_id" = "s1"."id" INNER JOIN "IMDB"."aka_name" ON "cast_info"."person_id" = "aka_name"."person_id") ON "person_info"."person_id" = "aka_name"."person_id") AS "t2" LEFT JOIN "IMDB"."movie_companies" ON "t2"."id1" = "movie_companies"."movie_id" LEFT JOIN "IMDB"."company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT JOIN "IMDB"."movie_keyword" ON "t2"."id1" = "movie_keyword"."movie_id" LEFT JOIN "IMDB"."keyword" ON "movie_keyword"."keyword_id" = "keyword"."id") ON "t1"."id" = "t2"."role_id"
ORDER BY "t2"."production_year" DESC NULLS FIRST, "t2"."name") AS "t4"