SELECT COALESCE("ACTOR_NAME", "ACTOR_NAME") AS "ACTOR_NAME", "ACTOR_IMDB_INDEX", "MOVIE_TITLE", "MOVIE_YEAR", "ROLE_ID", "COMPANY_TYPE", "COMPANY_NAME", "MOVIE_INFO", "MOVIE_KEYWORD"
FROM (SELECT "name" AS "ACTOR_NAME", "imdb_index" AS "ACTOR_IMDB_INDEX", "title" AS "MOVIE_TITLE", "production_year" AS "MOVIE_YEAR", "role_id" AS "ROLE_ID", "kind" AS "COMPANY_TYPE", "name0" AS "COMPANY_NAME", "info" AS "MOVIE_INFO", "keyword" AS "MOVIE_KEYWORD"
FROM "s1"
ORDER BY "production_year" DESC NULLS FIRST, "name"
FETCH NEXT 100 ROWS ONLY) AS "t8"