SELECT COALESCE("POSTTYPE", "POSTTYPE") AS "POSTTYPE", "TOTALPOSTS", "POSITIVESCOREDPOSTS", "TOTALVIEWS", "AVERAGESCORE", "AVERAGEANSWERCOUNT", "AVERAGECOMMENTCOUNT", "UNIQUEPOSTOWNERS", "AVERAGEUSERREPUTATION"
FROM (SELECT ANY_VALUE("Name") AS "POSTTYPE", COUNT(*) AS "TOTALPOSTS", SUM("FD_COL_1") AS "POSITIVESCOREDPOSTS", SUM("ViewCount") AS "TOTALVIEWS", AVG("Score") AS "AVERAGESCORE", AVG("FD_COL_4") AS "AVERAGEANSWERCOUNT", AVG("FD_COL_5") AS "AVERAGECOMMENTCOUNT", COUNT(DISTINCT "OwnerUserId") AS "UNIQUEPOSTOWNERS", AVG("Reputation") AS "AVERAGEUSERREPUTATION"
FROM "s1"
GROUP BY "Name"
ORDER BY 2 DESC NULLS FIRST) AS "t3"