SELECT COALESCE("Posts"."Id", "Posts"."Id") AS "POSTID", "Posts"."Title" AS "TITLE", "Posts"."CreationDate" AS "CREATIONDATE", "Users"."DisplayName" AS "OWNERDISPLAYNAME", CASE WHEN "t0"."UPVOTES" IS NOT NULL THEN CAST("t0"."UPVOTES" AS INTEGER) ELSE 0 END AS "UPVOTES", CASE WHEN "t0"."DOWNVOTES" IS NOT NULL THEN CAST("t0"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "DOWNVOTES", CASE WHEN "t0"."VOTECOUNT" IS NOT NULL THEN CAST("t0"."VOTECOUNT" AS BIGINT) ELSE 0 END AS "VOTECOUNT"
FROM "STACK"."Posts"
LEFT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
LEFT JOIN (SELECT "PostId" AS "POSTID", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES", COUNT(*) AS "VOTECOUNT"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t0" ON "Posts"."Id" = "t0"."POSTID"