SELECT COALESCE("Posts"."Id", "Posts"."Id") AS "POSTID", "Posts"."Title" AS "TITLE", "Posts"."CreationDate" AS "CREATIONDATE", "Posts"."ViewCount" AS "VIEWCOUNT", "PostTypes"."Name" AS "POSTTYPE", "Users"."DisplayName" AS "OWNERDISPLAYNAME", CASE WHEN "t0"."UPVOTESCOUNT" IS NOT NULL THEN CAST("t0"."UPVOTESCOUNT" AS INTEGER) ELSE 0 END AS "UPVOTESCOUNT", CASE WHEN "t0"."DOWNVOTESCOUNT" IS NOT NULL THEN CAST("t0"."DOWNVOTESCOUNT" AS INTEGER) ELSE 0 END AS "DOWNVOTESCOUNT", CASE WHEN "t2"."BADGECOUNT" IS NOT NULL THEN CAST("t2"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT"
FROM "STACK"."Posts"
INNER JOIN "STACK"."PostTypes" ON "Posts"."PostTypeId" = "PostTypes"."Id"
LEFT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
LEFT JOIN (SELECT "PostId" AS "POSTID", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTESCOUNT", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTESCOUNT"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t0" ON "Posts"."Id" = "t0"."POSTID"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "BADGECOUNT"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t2" ON "Users"."Id" = "t2"."USERID"