SELECT COALESCE(ANY_VALUE("Users"."Id"), ANY_VALUE("Users"."Id")) AS "USERID", "Users"."Reputation" AS "REPUTATION", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts"."Id") AS "POSTCOUNT", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONCOUNT", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERCOUNT", CASE WHEN SUM("t0"."COMMENTCOUNT") IS NOT NULL THEN CAST(SUM("t0"."COMMENTCOUNT") AS BIGINT) ELSE 0 END AS "TOTALCOMMENTS", CASE WHEN SUM("t2"."VOTECOUNT") IS NOT NULL THEN CAST(SUM("t2"."VOTECOUNT") AS BIGINT) ELSE 0 END AS "TOTALVOTES"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
LEFT JOIN (SELECT "PostId" AS "POSTID", COUNT(*) AS "COMMENTCOUNT"
FROM "STACK"."Comments"
GROUP BY "PostId") AS "t0" ON "Posts"."Id" = "t0"."POSTID"
LEFT JOIN (SELECT "PostId" AS "POSTID", COUNT(*) AS "VOTECOUNT"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t2" ON "Posts"."Id" = "t2"."POSTID"
GROUP BY "Users"."Id", "Users"."Reputation", "Users"."DisplayName"