SELECT COALESCE(ANY_VALUE("PostTypes"."Name"), ANY_VALUE("PostTypes"."Name")) AS "POSTTYPE", COUNT(*) AS "TOTALPOSTS", SUM("Posts"."Score") AS "TOTALSCORE", SUM("Posts"."ViewCount") AS "TOTALVIEWS", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALUPVOTES", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "TOTALDOWNVOTES", ROUND(CAST(SUM("Posts"."Score") AS DECIMAL(12, 1)) / CASE WHEN COUNT(*) = 0 THEN NULL ELSE COUNT(*) END, 2) AS "AVGSCOREPERPOST", ROUND(CAST(SUM("Posts"."ViewCount") AS DECIMAL(12, 1)) / CASE WHEN COUNT(*) = 0 THEN NULL ELSE COUNT(*) END, 2) AS "AVGVIEWSPERPOST"
FROM "STACK"."Posts"
INNER JOIN "STACK"."PostTypes" ON "Posts"."PostTypeId" = "PostTypes"."Id"
LEFT JOIN "STACK"."Votes" ON "Posts"."Id" = "Votes"."PostId"
GROUP BY "PostTypes"."Name"