SELECT COALESCE("Users"."Id", "Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", CASE WHEN "t1"."BADGECOUNT" IS NOT NULL THEN CAST("t1"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t1"."GOLDBADGES" IS NOT NULL THEN CAST("t1"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t1"."SILVERBADGES" IS NOT NULL THEN CAST("t1"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t1"."BRONZEBADGES" IS NOT NULL THEN CAST("t1"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t3"."POSTCOUNT" IS NOT NULL THEN CAST("t3"."POSTCOUNT" AS BIGINT) ELSE 0 END AS "POSTCOUNT", CASE WHEN "t3"."TOTALSCORE" IS NOT NULL THEN CAST("t3"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t3"."TOTALVIEWS" IS NOT NULL THEN CAST("t3"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t3"."AVGSCORE" IS NOT NULL THEN CAST("t3"."AVGSCORE" AS INTEGER) ELSE 0 END AS "AVGSCORE"
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Users" AS "Users0"
LEFT JOIN "STACK"."Badges" ON "Users0"."Id" = "Badges"."UserId"
GROUP BY "Users0"."Id") AS "t1" ON "Users"."Id" = "t1"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "POSTCOUNT", SUM("Score") AS "TOTALSCORE", SUM("ViewCount") AS "TOTALVIEWS", AVG("Score") AS "AVGSCORE"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t3" ON "Users"."Id" = "t3"."OWNERUSERID"