SELECT COALESCE("USERREPUTATION", "USERREPUTATION") AS "USERREPUTATION", "TOTALPOSTS", "TOTALQUESTIONS", "TOTALANSWERS", "TOTALVOTES", "AVERAGEPOSTSCORE", "MOSTRECENTPOST", "UNIQUEVOTERS"
FROM (SELECT ANY_VALUE("Reputation") AS "USERREPUTATION", COUNT("Id0") AS "TOTALPOSTS", SUM("FD_COL_3") AS "TOTALQUESTIONS", SUM("FD_COL_4") AS "TOTALANSWERS", SUM("FD_COL_5") AS "TOTALVOTES", AVG("Score") AS "AVERAGEPOSTSCORE", MAX("CreationDate0") AS "MOSTRECENTPOST", COUNT(DISTINCT "UserId") AS "UNIQUEVOTERS"
FROM "s1"
GROUP BY "Id", "Reputation"
ORDER BY 1 DESC NULLS FIRST) AS "t3"