SELECT COALESCE("t3"."POSTID", "t3"."POSTID") AS "POSTID", "t3"."POSTTYPEID", "t3"."CREATIONDATE", "t3"."SCORE", "t3"."VIEWCOUNT", "t3"."COMMENTCOUNT", "t3"."ANSWERCOUNT", "t3"."VOTECOUNT", "t6"."USERID", "t6"."GOLDBADGES", "t6"."SILVERBADGES", "t6"."BRONZEBADGES", "t6"."AVGREPUTATION"
FROM (SELECT "t2"."POSTID", "t2"."POSTTYPEID", "t2"."CREATIONDATE", "t2"."SCORE", "t2"."VIEWCOUNT", "t2"."COMMENTCOUNT", "t2"."ANSWERCOUNT", "t2"."VOTECOUNT", "Users"."Id", "Users"."Reputation", "Users"."CreationDate", "Users"."DisplayName", "Users"."LastAccessDate", "Users"."WebsiteUrl", "Users"."Location", "Users"."AboutMe", "Users"."Views", "Users"."UpVotes", "Users"."DownVotes", "Users"."ProfileImageUrl", "Users"."AccountId"
FROM (SELECT ANY_VALUE("Id") AS "POSTID", "POSTTYPEID", "CREATIONDATE", "SCORE", "VIEWCOUNT", COUNT("FD_COL_5") AS "COMMENTCOUNT", COUNT("FD_COL_6") AS "ANSWERCOUNT", COUNT(DISTINCT "Id2") AS "VOTECOUNT", CAST("POSTTYPEID" AS INTEGER) AS "POSTTYPEID0"
FROM "s1"
GROUP BY "Id", "POSTTYPEID", "CREATIONDATE", "SCORE", "VIEWCOUNT") AS "t2"
INNER JOIN "STACK"."Users" ON "t2"."POSTTYPEID0" = "Users"."AccountId") AS "t3"
INNER JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES", AVG("Users0"."Reputation") AS "AVGREPUTATION"
FROM "STACK"."Users" AS "Users0"
LEFT JOIN "STACK"."Badges" ON "Users0"."Id" = "Badges"."UserId"
GROUP BY "Users0"."Id") AS "t6" ON "t3"."Id" = "t6"."USERID"
ORDER BY "t3"."VIEWCOUNT" DESC NULLS FIRST, "t3"."SCORE" DESC NULLS FIRST