SELECT COALESCE("Posts"."Id", "Posts"."Id") AS "POSTID", "Posts"."Title" AS "TITLE", "Posts"."CreationDate" AS "POSTCREATIONDATE", "Users"."DisplayName" AS "OWNERDISPLAYNAME", "Users"."Reputation" AS "OWNERREPUTATION", CASE WHEN "t0"."UPVOTES" IS NOT NULL THEN CAST("t0"."UPVOTES" AS INTEGER) ELSE 0 END AS "UPVOTECOUNT", CASE WHEN "t0"."DOWNVOTES" IS NOT NULL THEN CAST("t0"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "DOWNVOTECOUNT", CASE WHEN "t2"."COMMENTCOUNT" IS NOT NULL THEN CAST("t2"."COMMENTCOUNT" AS BIGINT) ELSE 0 END AS "COMMENTCOUNT", CASE WHEN "t4"."BADGECOUNT" IS NOT NULL THEN CAST("t4"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "USERBADGECOUNT"
FROM "STACK"."Posts"
INNER JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
LEFT JOIN (SELECT "PostId" AS "POSTID", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t0" ON "Posts"."Id" = "t0"."POSTID"
LEFT JOIN (SELECT "PostId" AS "POSTID", COUNT(*) AS "COMMENTCOUNT"
FROM "STACK"."Comments"
GROUP BY "PostId") AS "t2" ON "Posts"."Id" = "t2"."POSTID"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "BADGECOUNT"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t4" ON "Users"."Id" = "t4"."USERID"
WHERE CAST("Posts"."PostTypeId" AS INTEGER) = 1