SELECT COALESCE("USERID", "USERID") AS "USERID", "DISPLAYNAME", "BADGECOUNT", "POSTCOUNT", "QUESTIONCOUNT", "ANSWERCOUNT", "TOTALSCORE", "AVERAGEVIEWS", "RECENTACTIVITYCOUNT"
FROM (SELECT "Users"."Id" AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t1"."BADGECOUNT" IS NOT NULL THEN CAST("t1"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t3"."POSTCOUNT" IS NOT NULL THEN CAST("t3"."POSTCOUNT" AS BIGINT) ELSE 0 END AS "POSTCOUNT", CASE WHEN "t3"."QUESTIONCOUNT" IS NOT NULL THEN CAST("t3"."QUESTIONCOUNT" AS BIGINT) ELSE 0 END AS "QUESTIONCOUNT", CASE WHEN "t3"."ANSWERCOUNT" IS NOT NULL THEN CAST("t3"."ANSWERCOUNT" AS BIGINT) ELSE 0 END AS "ANSWERCOUNT", CASE WHEN "t3"."TOTALSCORE" IS NOT NULL THEN CAST("t3"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t3"."AVERAGEVIEWS" IS NOT NULL THEN CAST("t3"."AVERAGEVIEWS" AS INTEGER) ELSE 0 END AS "AVERAGEVIEWS", CASE WHEN "t5"."RECENTACTIVITYCOUNT" IS NOT NULL THEN CAST("t5"."RECENTACTIVITYCOUNT" AS BIGINT) ELSE 0 END AS "RECENTACTIVITYCOUNT"
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Users" AS "Users0"
LEFT JOIN "STACK"."Badges" ON "Users0"."Id" = "Badges"."UserId"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t1" ON "Users"."Id" = "t1"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "POSTCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERCOUNT", SUM("Score") AS "TOTALSCORE", AVG("ViewCount") AS "AVERAGEVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t3" ON "Users"."Id" = "t3"."OWNERUSERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(CASE WHEN "LastActivityDate" >= (TIMESTAMP '2024-10-01 12:34:56' - INTERVAL '30' DAY) THEN 1 ELSE NULL END) AS "RECENTACTIVITYCOUNT"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t5" ON "Users"."Id" = "t5"."OWNERUSERID") AS "t6"
WHERE "t6"."BADGECOUNT" > 0 AND ("t6"."TOTALSCORE" >= (((SELECT AVG(SUM("Score"))
FROM "STACK"."Posts"
GROUP BY "OwnerUserId"))) OR "t6"."RECENTACTIVITYCOUNT" > 5)