SELECT COALESCE("DISPLAYNAME", "DISPLAYNAME") AS "DISPLAYNAME", "BADGECOUNT", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES", "POSTCOUNT", "TOTALVIEWS", "TOTALSCORE", "AVGSCORE"
FROM (SELECT "t1"."DISPLAYNAME", "t1"."BADGECOUNT", "t1"."GOLDBADGES", "t1"."SILVERBADGES", "t1"."BRONZEBADGES", "t3"."POSTCOUNT", "t3"."TOTALVIEWS", "t3"."TOTALSCORE", "t3"."AVGSCORE"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Badges" ON "Users"."Id" = "Badges"."UserId"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t1"
INNER JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "POSTCOUNT", SUM("ViewCount") AS "TOTALVIEWS", SUM("Score") AS "TOTALSCORE", AVG("Score") AS "AVGSCORE"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t3" ON "t1"."USERID" = "t3"."OWNERUSERID") AS "t4"
WHERE "t4"."BADGECOUNT" > 0 AND "t4"."POSTCOUNT" > 10