SELECT COALESCE("Users0"."Id", "Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", "t1"."BADGECOUNT", "t1"."GOLDBADGES", "t1"."SILVERBADGES", "t1"."BRONZEBADGES", "t3"."TOTALPOSTS", "t3"."QUESTIONS", "t3"."ANSWERS", "t3"."TAGWIKIS", "t3"."TOTALVIEWS", "t3"."TOTALSCORE", CASE WHEN "t3"."TOTALPOSTS" > 0 THEN "t3"."TOTALSCORE" / CAST("t3"."TOTALPOSTS" AS FLOAT) ELSE 0E0 END AS "AVGSCOREPERPOST"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Badges" ON "Users"."Id" = "Badges"."UserId"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t1"
INNER JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) IN (4, 5) THEN 1 ELSE NULL END) AS "TAGWIKIS", SUM("ViewCount") AS "TOTALVIEWS", SUM("Score") AS "TOTALSCORE"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t3" ON "t1"."USERID" = "t3"."OWNERUSERID"
INNER JOIN "STACK"."Users" AS "Users0" ON "t1"."USERID" = "Users0"."Id"