SELECT COALESCE("Users"."Id", "Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t1"."BADGECOUNT" IS NOT NULL THEN CAST("t1"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t1"."GOLDBADGES" IS NOT NULL THEN CAST("t1"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t1"."SILVERBADGES" IS NOT NULL THEN CAST("t1"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t1"."BRONZEBADGES" IS NOT NULL THEN CAST("t1"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t3"."QUESTIONCOUNT" IS NOT NULL THEN CAST("t3"."QUESTIONCOUNT" AS INTEGER) ELSE 0 END AS "QUESTIONCOUNT", CASE WHEN "t3"."ANSWERCOUNT" IS NOT NULL THEN CAST("t3"."ANSWERCOUNT" AS INTEGER) ELSE 0 END AS "ANSWERCOUNT", CASE WHEN "t3"."AVGSCORE" IS NOT NULL THEN CAST("t3"."AVGSCORE" AS INTEGER) ELSE 0 END AS "AVGSCORE", CASE WHEN "t3"."TOTALVIEWS" IS NOT NULL THEN CAST("t3"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t5"."TOTALEDITS" IS NOT NULL THEN CAST("t5"."TOTALEDITS" AS BIGINT) ELSE 0 END AS "TOTALEDITS", CASE WHEN "t5"."TITLEEDITS" IS NOT NULL THEN CAST("t5"."TITLEEDITS" AS INTEGER) ELSE 0 END AS "TITLEEDITS", CASE WHEN "t5"."POSTSCLOSED" IS NOT NULL THEN CAST("t5"."POSTSCLOSED" AS INTEGER) ELSE 0 END AS "POSTSCLOSED", CASE WHEN "t5"."POSTSDELETED" IS NOT NULL THEN CAST("t5"."POSTSDELETED" AS INTEGER) ELSE 0 END AS "POSTSDELETED"
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Users" AS "Users0"
LEFT JOIN "STACK"."Badges" ON "Users0"."Id" = "Badges"."UserId"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t1" ON "Users"."Id" = "t1"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONCOUNT", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERCOUNT", AVG("Score") AS "AVGSCORE", SUM("ViewCount") AS "TOTALVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t3" ON "Users"."Id" = "t3"."OWNERUSERID"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALEDITS", SUM(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) IN (4, 5, 6) THEN 1 ELSE 0 END) AS "TITLEEDITS", SUM(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 10 THEN 1 ELSE 0 END) AS "POSTSCLOSED", SUM(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 12 THEN 1 ELSE 0 END) AS "POSTSDELETED"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t5" ON "Users"."Id" = "t5"."USERID"