SELECT COALESCE("Users"."Id", "Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t1"."BADGECOUNT" IS NOT NULL THEN CAST("t1"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t1"."GOLDBADGES" IS NOT NULL THEN CAST("t1"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t1"."SILVERBADGES" IS NOT NULL THEN CAST("t1"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t1"."BRONZEBADGES" IS NOT NULL THEN CAST("t1"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t3"."TOTALCOMMENTS" IS NOT NULL THEN CAST("t3"."TOTALCOMMENTS" AS BIGINT) ELSE 0 END AS "TOTALCOMMENTS", CASE WHEN "t3"."TOTALVOTES" IS NOT NULL THEN CAST("t3"."TOTALVOTES" AS BIGINT) ELSE 0 END AS "TOTALVOTES", CASE WHEN "t3"."TOTALVIEWS" IS NOT NULL THEN CAST("t3"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t3"."TOTALSCORE" IS NOT NULL THEN CAST("t3"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CAST(CASE WHEN "t3"."TOTALSCORE" IS NOT NULL THEN CAST("t3"."TOTALSCORE" AS INTEGER) ELSE 0 END AS DECIMAL(12, 1)) / CASE WHEN CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END = 0 THEN NULL ELSE CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END END AS "SCOREPERPOST", CAST(CASE WHEN "t3"."TOTALVIEWS" IS NOT NULL THEN CAST("t3"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS DECIMAL(12, 1)) / CASE WHEN CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END = 0 THEN NULL ELSE CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END END AS "VIEWSPERPOST"
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Users" AS "Users0"
LEFT JOIN "STACK"."Badges" ON "Users0"."Id" = "Badges"."UserId"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t1" ON "Users"."Id" = "t1"."USERID"
LEFT JOIN (SELECT "Posts"."OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", COUNT("Comments"."Id") AS "TOTALCOMMENTS", COUNT(DISTINCT "Votes"."Id") AS "TOTALVOTES", SUM("Posts"."ViewCount") AS "TOTALVIEWS", SUM("Posts"."Score") AS "TOTALSCORE"
FROM "STACK"."Posts"
LEFT JOIN "STACK"."Comments" ON "Posts"."Id" = "Comments"."PostId"
LEFT JOIN "STACK"."Votes" ON "Posts"."Id" = "Votes"."PostId"
GROUP BY "Posts"."OwnerUserId") AS "t3" ON "Users"."Id" = "t3"."OWNERUSERID"