SELECT COALESCE("t3"."USERID", "t3"."USERID") AS "USERID", "t3"."DISPLAYNAME", "t3"."TOTALREPUTATION", "t3"."BADGECOUNT", "t3"."CLOSEDPOSTS", "Posts0"."Id" AS "POSTID", "Posts0"."Title" AS "TITLE", "Posts0"."CreationDate" AS "POSTCREATIONDATE", CASE WHEN "t5"."TOTALUPVOTES" IS NOT NULL THEN CAST("t5"."TOTALUPVOTES" AS INTEGER) ELSE 0 END AS "POSTUPVOTES", CASE WHEN "t5"."TOTALDOWNVOTES" IS NOT NULL THEN CAST("t5"."TOTALDOWNVOTES" AS INTEGER) ELSE 0 END AS "POSTDOWNVOTES", CASE WHEN "t5"."AVGVOTETYPE" IS NOT NULL THEN CAST("t5"."AVGVOTETYPE" AS INTEGER) ELSE 0 END AS "POSTAVGVOTE"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", SUM("Users"."Reputation") AS "TOTALREPUTATION", COUNT("Badges"."Id") AS "BADGECOUNT", COUNT(DISTINCT "t0"."POSTID") AS "CLOSEDPOSTS"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Badges" ON "Users"."Id" = "Badges"."UserId"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
LEFT JOIN (SELECT "PostId" AS "POSTID", "CreationDate" AS "CREATIONDATE", "PostHistoryTypeId" AS "POSTHISTORYTYPEID", ROW_NUMBER() OVER (PARTITION BY "PostId" ORDER BY "CreationDate" DESC NULLS FIRST) AS "REVISIONNUMBER"
FROM "STACK"."PostHistory"
WHERE CAST("PostHistoryTypeId" AS INTEGER) = 10 OR CAST("PostHistoryTypeId" AS INTEGER) = 11 OR CAST("PostHistoryTypeId" AS INTEGER) = 12) AS "t0" ON "Posts"."Id" = "t0"."POSTID"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t3"
INNER JOIN "STACK"."Posts" AS "Posts0" ON "t3"."USERID" = "Posts0"."OwnerUserId"
LEFT JOIN (SELECT "PostId" AS "POSTID", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALUPVOTES", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "TOTALDOWNVOTES", AVG(CASE WHEN CAST("VoteTypeId" AS INTEGER) IN (2, 3) THEN "VoteTypeId" ELSE NULL END) AS "AVGVOTETYPE"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t5" ON "Posts0"."Id" = "t5"."POSTID"