SELECT COALESCE("t1"."USERID", "t1"."USERID") AS "USERID", "t1"."DISPLAYNAME", "t1"."TOTALPOSTS", "t1"."TOTALBOUNTY", "t1"."GOLDBADGES", "t1"."SILVERBADGES", "t1"."BRONZEBADGES", COUNT("t3"."ID") AS "RECENTPOSTSCOUNT", AVG("t3"."SCORE") AS "AVERAGERECENTPOSTSCORE", AVG(CASE WHEN "t3"."SCORE" IS NOT NULL THEN CAST("t3"."SCORE" AS INTEGER) ELSE 0 END) AS "FD_COL_9"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT(DISTINCT "Posts"."Id") AS "TOTALPOSTS", SUM(CASE WHEN "Votes"."BountyAmount" IS NOT NULL THEN CAST("Votes"."BountyAmount" AS INTEGER) ELSE 0 END) AS "TOTALBOUNTY", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
LEFT JOIN "STACK"."Badges" ON "Users"."Id" = "Badges"."UserId"
LEFT JOIN "STACK"."Votes" ON "Posts"."Id" = "Votes"."PostId" AND CAST("Votes"."VoteTypeId" AS INTEGER) = 8
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t1"
LEFT JOIN (SELECT "Id" AS "ID", "Title" AS "TITLE", "CreationDate" AS "CREATIONDATE", "Score" AS "SCORE", "ViewCount" AS "VIEWCOUNT", ROW_NUMBER() OVER (PARTITION BY "OwnerUserId" ORDER BY "CreationDate" DESC NULLS FIRST) AS "POSTRANK", "OwnerUserId" AS "OWNERUSERID"
FROM "STACK"."Posts"
WHERE "CreationDate" >= (TIMESTAMP '2024-10-01 12:34:56' - INTERVAL '1' YEAR) AND "Score" > 10) AS "t3" ON "t1"."USERID" = "t3"."OWNERUSERID"
GROUP BY "t1"."USERID", "t1"."DISPLAYNAME", "t1"."TOTALPOSTS", "t1"."TOTALBOUNTY", "t1"."GOLDBADGES", "t1"."SILVERBADGES", "t1"."BRONZEBADGES"