SELECT COALESCE("t3"."USERID", "t3"."USERID") AS "USERID", "t3"."DISPLAYNAME", "t3"."POSTCOUNT" AS "TOTALPOSTS", CASE WHEN "t6"."VOTECOUNT" IS NOT NULL THEN CAST("t6"."VOTECOUNT" AS BIGINT) ELSE 0 END AS "TOTALVOTES", CASE WHEN "t6"."UPVOTES" IS NOT NULL THEN CAST("t6"."UPVOTES" AS INTEGER) ELSE 0 END AS "TOTALUPVOTES", CASE WHEN "t6"."DOWNVOTES" IS NOT NULL THEN CAST("t6"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "TOTALDOWNVOTES", CASE WHEN "t9"."BADGECOUNT" IS NOT NULL THEN CAST("t9"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t9"."HIGHESTBADGELEVEL" IS NOT NULL THEN CAST("t9"."HIGHESTBADGELEVEL" AS INTEGER) ELSE 0 END AS "HIGHESTBADGELEVEL", CASE WHEN CAST("t9"."HIGHESTBADGELEVEL" AS INTEGER) = 1 THEN 'Gold    ' WHEN CAST("t9"."HIGHESTBADGELEVEL" AS INTEGER) = 2 THEN 'Silver  ' WHEN CAST("t9"."HIGHESTBADGELEVEL" AS INTEGER) = 3 THEN 'Bronze  ' ELSE 'No Badge' END AS "BADGELEVELDESCRIPTION"
FROM (SELECT "USERID", "DISPLAYNAME", "POSTCOUNT", RANK() OVER (ORDER BY "POSTCOUNT" DESC NULLS FIRST) AS "USERRANK"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts"."Id") AS "POSTCOUNT"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t1"
WHERE "t1"."POSTCOUNT" > 0) AS "t3"
LEFT JOIN (SELECT ANY_VALUE("Posts0"."Id") AS "POSTID", COUNT("Votes"."Id") AS "VOTECOUNT", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."Posts" AS "Posts0"
LEFT JOIN "STACK"."Votes" ON "Posts0"."Id" = "Votes"."PostId"
GROUP BY "Posts0"."Id") AS "t6" ON "t3"."USERID" = "t6"."POSTID"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", COUNT("Badges"."Id") AS "BADGECOUNT", MAX("Badges"."Class") AS "HIGHESTBADGELEVEL"
FROM "STACK"."Users" AS "Users0"
LEFT JOIN "STACK"."Badges" ON "Users0"."Id" = "Badges"."UserId"
GROUP BY "Users0"."Id") AS "t9" ON "t3"."USERID" = "t9"."USERID"