SELECT COALESCE("Id", "Id") AS "POSTID", "Title" AS "TITLE", "CreationDate" AS "CREATIONDATE", "Score" AS "SCORE", "ViewCount" AS "VIEWCOUNT", "AnswerCount" AS "ANSWERCOUNT", "OwnerUserId" AS "OWNERUSERID", ROW_NUMBER() OVER (PARTITION BY "OwnerUserId" ORDER BY "CreationDate" DESC NULLS FIRST) AS "RN"
FROM "STACK"."Posts"
WHERE "CreationDate" >= (CAST('2024-10-01 12:34:56' AS TIMESTAMP(0)) - INTERVAL '30' DAY)