SELECT COALESCE("t1"."USERID", "t1"."USERID") AS "USERID", "t1"."DISPLAYNAME", "t1"."REPUTATION", "t1"."TOTALPOSTS", "t1"."TOTALQUESTIONS", "t1"."TOTALANSWERS", "t1"."ACCEPTEDANSWERS", "t3"."GOLDBADGES", "t3"."SILVERBADGES", "t3"."BRONZEBADGES", "t5"."TOTALEDITS", "t5"."LASTEDITDATE"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", COUNT(DISTINCT "Posts"."Id") AS "TOTALPOSTS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "TOTALQUESTIONS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALANSWERS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 AND "Posts"."AcceptedAnswerId" IS NOT NULL THEN 1 ELSE 0 END) AS "ACCEPTEDANSWERS"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
GROUP BY "Users"."Id", "Users"."DisplayName", "Users"."Reputation") AS "t1"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "GOLDBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "SILVERBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE NULL END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t3" ON "t1"."USERID" = "t3"."USERID"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALEDITS", MAX("CreationDate") AS "LASTEDITDATE"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t5" ON "t1"."USERID" = "t5"."USERID"
WHERE "t1"."REPUTATION" > 100