SELECT COALESCE("USERID", "USERID") AS "USERID", "DISPLAYNAME", "REPUTATION", "POSTCOUNT", "POSITIVEPOSTS", "NEGATIVEPOSTS", "POPULARPOSTS", "AVERAGEVIEWS", "BADGECOUNT", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES"
FROM (SELECT "t1"."USERID", "t1"."DISPLAYNAME", "t1"."REPUTATION", "t1"."POSTCOUNT", "t1"."POSITIVEPOSTS", "t1"."NEGATIVEPOSTS", "t1"."POPULARPOSTS", "t1"."AVERAGEVIEWS", CASE WHEN "t3"."BADGECOUNT" IS NOT NULL THEN CAST("t3"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t3"."GOLDBADGES" IS NOT NULL THEN CAST("t3"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t3"."SILVERBADGES" IS NOT NULL THEN CAST("t3"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t3"."BRONZEBADGES" IS NOT NULL THEN CAST("t3"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", COUNT(DISTINCT "Posts"."Id") AS "POSTCOUNT", SUM(CASE WHEN "Posts"."Score" > 0 THEN 1 ELSE 0 END) AS "POSITIVEPOSTS", SUM(CASE WHEN "Posts"."Score" < 0 THEN 1 ELSE 0 END) AS "NEGATIVEPOSTS", SUM(CASE WHEN "Posts"."ViewCount" > 100 THEN 1 ELSE 0 END) AS "POPULARPOSTS", AVG("Posts"."ViewCount") AS "AVERAGEVIEWS"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
GROUP BY "Users"."Id", "Users"."DisplayName", "Users"."Reputation") AS "t1"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "BADGECOUNT", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t3" ON "t1"."USERID" = "t3"."USERID") AS "t4"
WHERE "t4"."REPUTATION" > 1000