SELECT COALESCE("t1"."USERID", "t1"."USERID") AS "USERID", "t1"."DISPLAYNAME", "t1"."TOTALPOSTS", "t1"."QUESTIONCOUNT", "t1"."ANSWERCOUNT", "t1"."TOTALVIEWS", "t1"."TOTALSCORE", "t1"."LASTPOSTDATE", "t3"."USERID" AS "USERID0", "t3"."GOLDBADGES", "t3"."SILVERBADGES", "t3"."BRONZEBADGES"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts"."Id") AS "TOTALPOSTS", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN "Posts"."Id" ELSE NULL END) AS "QUESTIONCOUNT", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN "Posts"."Id" ELSE NULL END) AS "ANSWERCOUNT", SUM("Posts"."ViewCount") AS "TOTALVIEWS", SUM("Posts"."Score") AS "TOTALSCORE", MAX("Posts"."CreationDate") AS "LASTPOSTDATE"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t1"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "GOLDBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "SILVERBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE NULL END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t3" ON "t1"."USERID" = "t3"."USERID"