SELECT COALESCE("t1"."DISPLAYNAME", "t1"."DISPLAYNAME") AS "DISPLAYNAME", "t1"."REPUTATION", "t1"."BADGECOUNT", "t1"."UPVOTESRECEIVED", "t1"."DOWNVOTESRECEIVED", "t3"."TOTALPOSTS", "t3"."QUESTIONS" AS "TOTALQUESTIONS", "t3"."ANSWERS" AS "TOTALANSWERS", "t3"."AVGPOSTSCORE", "t3"."TOTALVIEWS"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTESRECEIVED", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTESRECEIVED"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Badges" ON "Users"."Id" = "Badges"."UserId"
LEFT JOIN "STACK"."Votes" ON "Users"."Id" = "Votes"."UserId"
GROUP BY "Users"."Id", "Users"."DisplayName", "Users"."Reputation") AS "t1"
INNER JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", COUNT(DISTINCT CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN "Id" ELSE NULL END) AS "QUESTIONS", COUNT(DISTINCT CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN "Id" ELSE NULL END) AS "ANSWERS", AVG("Score") AS "AVGPOSTSCORE", SUM("ViewCount") AS "TOTALVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t3" ON "t1"."USERID" = "t3"."OWNERUSERID"