SELECT COALESCE("t1"."USERID", "t1"."USERID") AS "USERID", "t1"."DISPLAYNAME", "t1"."REPUTATION", "t1"."TOTALPOSTS", "t1"."TOTALQUESTIONS", "t1"."TOTALANSWERS", "t1"."TOTALUPVOTES", "t1"."TOTALDOWNVOTES", CASE WHEN "t3"."TOTALBADGES" IS NOT NULL THEN CAST("t3"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t3"."GOLDBADGES" IS NOT NULL THEN CAST("t3"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t3"."SILVERBADGES" IS NOT NULL THEN CAST("t3"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t3"."BRONZEBADGES" IS NOT NULL THEN CAST("t3"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t5"."TOTALEDITS" IS NOT NULL THEN CAST("t5"."TOTALEDITS" AS BIGINT) ELSE 0 END AS "TOTALEDITS", CASE WHEN "t5"."TOTALCLOSEDPOSTS" IS NOT NULL THEN CAST("t5"."TOTALCLOSEDPOSTS" AS BIGINT) ELSE 0 END AS "TOTALCLOSEDPOSTS"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", COUNT(DISTINCT "Posts"."Id") AS "TOTALPOSTS", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN "Posts"."Id" ELSE NULL END) AS "TOTALANSWERS", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN "Posts"."Id" ELSE NULL END) AS "TOTALQUESTIONS", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALUPVOTES", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "TOTALDOWNVOTES"
FROM "STACK"."Users"
LEFT JOIN "STACK"."Posts" ON "Users"."Id" = "Posts"."OwnerUserId"
LEFT JOIN "STACK"."Votes" ON "Posts"."Id" = "Votes"."PostId"
GROUP BY "Users"."Id", "Users"."DisplayName", "Users"."Reputation") AS "t1"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t3" ON "t1"."USERID" = "t3"."USERID"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALEDITS", COUNT(DISTINCT CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 10 THEN "Id" ELSE NULL END) AS "TOTALCLOSEDPOSTS"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t5" ON "t1"."USERID" = "t5"."USERID"