SELECT COALESCE("DISPLAYNAME", "DISPLAYNAME") AS "DISPLAYNAME", "UPVOTES", "DOWNVOTES", "TOTALPOSTS", "TOTALSCORE", "TOTALVIEWS"
FROM (SELECT "Users"."Id" AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t0"."UPVOTES" IS NOT NULL THEN CAST("t0"."UPVOTES" AS BIGINT) ELSE 0 END AS "UPVOTES", CASE WHEN "t0"."DOWNVOTES" IS NOT NULL THEN CAST("t0"."DOWNVOTES" AS BIGINT) ELSE 0 END AS "DOWNVOTES", CASE WHEN "t2"."TOTALPOSTS" IS NOT NULL THEN CAST("t2"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t2"."TOTALSCORE" IS NOT NULL THEN CAST("t2"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t2"."TOTALVIEWS" IS NOT NULL THEN CAST("t2"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS"
FROM "STACK"."Users"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "UPVOTES", COUNT(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE NULL END) AS "DOWNVOTES"
FROM "STACK"."Votes"
GROUP BY "UserId") AS "t0" ON "Users"."Id" = "t0"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", SUM("Score") AS "TOTALSCORE", SUM("ViewCount") AS "TOTALVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t2" ON "Users"."Id" = "t2"."OWNERUSERID") AS "t3"
WHERE "t3"."TOTALPOSTS" > 0