SELECT COALESCE("Users"."Id", "Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", CASE WHEN "t0"."TOTALVOTES" IS NOT NULL THEN CAST("t0"."TOTALVOTES" AS BIGINT) ELSE 0 END AS "TOTALVOTES", CASE WHEN "t0"."UPVOTES" IS NOT NULL THEN CAST("t0"."UPVOTES" AS INTEGER) ELSE 0 END AS "UPVOTES", CASE WHEN "t0"."DOWNVOTES" IS NOT NULL THEN CAST("t0"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "DOWNVOTES", CASE WHEN "t2"."TOTALBADGES" IS NOT NULL THEN CAST("t2"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t2"."GOLDBADGES" IS NOT NULL THEN CAST("t2"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t2"."SILVERBADGES" IS NOT NULL THEN CAST("t2"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t2"."BRONZEBADGES" IS NOT NULL THEN CAST("t2"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t4"."TOTALPOSTS" IS NOT NULL THEN CAST("t4"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t4"."QUESTIONS" IS NOT NULL THEN CAST("t4"."QUESTIONS" AS INTEGER) ELSE 0 END AS "QUESTIONS", CASE WHEN "t4"."ANSWERS" IS NOT NULL THEN CAST("t4"."ANSWERS" AS INTEGER) ELSE 0 END AS "ANSWERS", CASE WHEN "t4"."TOTALVIEWS" IS NOT NULL THEN CAST("t4"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t4"."TOTALSCORE" IS NOT NULL THEN CAST("t4"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE"
FROM "STACK"."Users"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALVOTES", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."Votes"
GROUP BY "UserId") AS "t0" ON "Users"."Id" = "t0"."USERID"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t2" ON "Users"."Id" = "t2"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERS", SUM("ViewCount") AS "TOTALVIEWS", SUM("Score") AS "TOTALSCORE"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t4" ON "Users"."Id" = "t4"."OWNERUSERID"
WHERE "Users"."Reputation" > 1000