SELECT COALESCE("t9"."POSTID", "t9"."POSTID") AS "POSTID", "t9"."TITLE", "t9"."OWNERDISPLAYNAME", "t9"."CREATIONDATE", "t9"."SCORE", "t9"."VIEWCOUNT", "t9"."COMMENTCOUNT", "t9"."ANSWERCOUNT", "t9"."UPVOTES", "t9"."DOWNVOTES", "t9"."EDITCOUNT", "t9"."CLOSECOUNT", "t9"."REOPENCOUNT", "t9"."DELETECOUNT"
FROM (SELECT "t7"."POSTID", "t7"."TITLE", "t7"."OWNERDISPLAYNAME", "t7"."CREATIONDATE", "t7"."SCORE", "t7"."VIEWCOUNT", "t7"."COMMENTCOUNT", "t7"."ANSWERCOUNT", "t7"."UPVOTES", "t7"."DOWNVOTES", "t5"."EDITCOUNT", "t5"."CLOSECOUNT", "t5"."REOPENCOUNT", "t5"."DELETECOUNT"
FROM (SELECT "PostId" AS "POSTID", COUNT(*) AS "EDITCOUNT", COUNT(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 10 THEN 1 ELSE NULL END) AS "CLOSECOUNT", COUNT(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 11 THEN 1 ELSE NULL END) AS "REOPENCOUNT", COUNT(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 12 THEN 1 ELSE NULL END) AS "DELETECOUNT"
FROM "STACK"."PostHistory"
GROUP BY "PostId") AS "t5"
RIGHT JOIN (SELECT ANY_VALUE("Id") AS "POSTID", "TITLE", ANY_VALUE("DisplayName") AS "OWNERDISPLAYNAME", "CREATIONDATE", "SCORE", "VIEWCOUNT", COUNT("FD_COL_6") AS "COMMENTCOUNT", COUNT("Id2") AS "ANSWERCOUNT", SUM("FD_COL_8") AS "UPVOTES", SUM("FD_COL_9") AS "DOWNVOTES"
FROM "s1"
GROUP BY "Id", "TITLE", "DisplayName", "CREATIONDATE", "SCORE", "VIEWCOUNT") AS "t7" ON "t5"."POSTID" = "t7"."POSTID"
ORDER BY "t7"."VIEWCOUNT" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t9"