SELECT COALESCE("PostTypes"."Name", "PostTypes"."Name") AS "Name", CASE WHEN "t1"."UPVOTES" IS NOT NULL THEN CAST("t1"."UPVOTES" AS INTEGER) ELSE 0 END AS "FD_COL_1", CASE WHEN "t1"."DOWNVOTES" IS NOT NULL THEN CAST("t1"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "FD_COL_2", CASE WHEN "t"."COMMENTCOUNT" IS NOT NULL THEN CAST("t"."COMMENTCOUNT" AS BIGINT) ELSE 0 END AS "FD_COL_3", "Posts"."ViewCount" AS "FD_COL_4", "Posts"."Score"
FROM "STACK"."Posts"
INNER JOIN "STACK"."PostTypes" ON "Posts"."PostTypeId" = "PostTypes"."Id"
LEFT JOIN (SELECT "PostId", COUNT(*) AS "COMMENTCOUNT"
FROM "STACK"."Comments"
GROUP BY "PostId") AS "t" ON "Posts"."Id" = "t"."PostId"
LEFT JOIN (SELECT "PostId" AS "POSTID", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t1" ON "Posts"."Id" = "t1"."POSTID"