SELECT COALESCE("t7"."POSTID", "t7"."POSTID") AS "POSTID", "t7"."TITLE", "t7"."CREATIONDATE", "t7"."VIEWCOUNT", "t7"."SCORE", "t7"."ANSWERCOUNT", "t7"."COMMENTCOUNT", "t7"."OWNERDISPLAYNAME", "t7"."UPVOTES", "t7"."DOWNVOTES", "t7"."CLOSECOUNT", "t7"."REOPENCOUNT"
FROM (SELECT "t5"."POSTID", "t5"."TITLE", "t5"."CREATIONDATE", "t5"."VIEWCOUNT", "t5"."SCORE", "t5"."ANSWERCOUNT", "t5"."COMMENTCOUNT", "t5"."OWNERDISPLAYNAME", "t5"."UPVOTES", "t5"."DOWNVOTES", CASE WHEN "s1"."CLOSECOUNT" IS NOT NULL THEN CAST("s1"."CLOSECOUNT" AS BIGINT) ELSE 0 END AS "CLOSECOUNT", CASE WHEN "s1"."REOPENCOUNT" IS NOT NULL THEN CAST("s1"."REOPENCOUNT" AS BIGINT) ELSE 0 END AS "REOPENCOUNT"
FROM (SELECT ANY_VALUE("t2"."Id") AS "POSTID", "t2"."Title" AS "TITLE", "t2"."CreationDate" AS "CREATIONDATE", "t2"."ViewCount" AS "VIEWCOUNT", "t2"."Score" AS "SCORE", "t2"."AnswerCount" AS "ANSWERCOUNT", COUNT(CASE WHEN "Comments"."Id" IS NOT NULL THEN 1 ELSE NULL END) AS "COMMENTCOUNT", SUM(CASE WHEN CAST("t2"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("t2"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES", ANY_VALUE("t2"."DisplayName") AS "OWNERDISPLAYNAME"
FROM (SELECT "Posts"."Id", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate", "Posts"."Score", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense", "Users"."Id" AS "Id0", "Users"."Reputation", "Users"."CreationDate" AS "CreationDate0", "Users"."DisplayName", "Users"."LastAccessDate", "Users"."WebsiteUrl", "Users"."Location", "Users"."AboutMe", "Users"."Views", "Users"."UpVotes", "Users"."DownVotes", "Users"."ProfileImageUrl", "Users"."AccountId", "Votes"."Id" AS "Id1", "Votes"."PostId", "Votes"."VoteTypeId", "Votes"."UserId", "Votes"."CreationDate" AS "CreationDate1", "Votes"."BountyAmount"
FROM "STACK"."Votes"
RIGHT JOIN ("STACK"."Posts" LEFT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id") ON "Votes"."PostId" = "Posts"."Id") AS "t2"
LEFT JOIN "STACK"."Comments" ON "t2"."Id" = "Comments"."PostId"
GROUP BY "t2"."Id", "t2"."Title", "t2"."CreationDate", "t2"."ViewCount", "t2"."Score", "t2"."AnswerCount", "t2"."DisplayName") AS "t5"
LEFT JOIN "s1" ON "t5"."POSTID" = "s1"."POSTID"
ORDER BY "t5"."SCORE" DESC NULLS FIRST, "t5"."VIEWCOUNT" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t7"