SELECT COALESCE("PostTypes"."Name", "PostTypes"."Name") AS "Name", "Posts"."ViewCount", CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END AS "FD_COL_2", CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END AS "FD_COL_3"
FROM "STACK"."Votes"
RIGHT JOIN ("STACK"."Posts" INNER JOIN "STACK"."PostTypes" ON "Posts"."PostTypeId" = "PostTypes"."Id") ON "Votes"."PostId" = "Posts"."Id"