SELECT COALESCE("t6"."USERID", "t6"."USERID") AS "USERID", "t6"."DISPLAYNAME", "t6"."POSTCOUNT", "t6"."TOTALSCORE", "t6"."TOTALVIEWS", "t6"."QUESTIONCOUNT", "t6"."ANSWERCOUNT", "t6"."EDITCOUNT", "t6"."BODYEDITS", "t6"."TITLEEDITS"
FROM (SELECT "t4"."USERID", "t4"."DISPLAYNAME", "t4"."POSTCOUNT", "t4"."TOTALSCORE", "t4"."TOTALVIEWS", "t4"."QUESTIONCOUNT", "t4"."ANSWERCOUNT", "t2"."EDITCOUNT", "t2"."BODYEDITS", "t2"."TITLEEDITS"
FROM (SELECT "PostHistory"."PostId" AS "POSTID", COUNT(*) AS "EDITCOUNT", SUM(CASE WHEN "PostHistoryTypes"."Name" = 'Edit Body' THEN 1 ELSE 0 END) AS "BODYEDITS", SUM(CASE WHEN "PostHistoryTypes"."Name" = 'Edit Title' THEN 1 ELSE 0 END) AS "TITLEEDITS"
FROM "STACK"."PostHistoryTypes"
INNER JOIN "STACK"."PostHistory" ON "PostHistoryTypes"."Id" = "PostHistory"."PostHistoryTypeId"
GROUP BY "PostHistory"."PostId") AS "t2"
RIGHT JOIN (SELECT ANY_VALUE("Id") AS "USERID", "DISPLAYNAME", COUNT("Id0") AS "POSTCOUNT", SUM("FD_COL_3") AS "TOTALSCORE", SUM("FD_COL_4") AS "TOTALVIEWS", SUM("FD_COL_5") AS "QUESTIONCOUNT", SUM("FD_COL_6") AS "ANSWERCOUNT"
FROM "s1"
GROUP BY "Id", "DISPLAYNAME") AS "t4" ON "t2"."POSTID" = "t4"."USERID"
ORDER BY "t4"."TOTALSCORE" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t6"