SELECT COALESCE("t6"."POSTID", "t6"."POSTID") AS "POSTID", "t6"."TITLE", "t6"."CREATIONDATE", "t6"."VIEWCOUNT", "t6"."SCORE", "t6"."OWNERDISPLAYNAME", "t6"."COMMENTCOUNT", "t6"."BADGECOUNT", "t6"."LASTHISTORYUPDATE", "t6"."TOTALVOTES", "t6"."UPVOTES", "t6"."DOWNVOTES"
FROM (SELECT "t2"."POSTID", "t2"."TITLE", "t2"."CREATIONDATE", "t2"."VIEWCOUNT", "t2"."SCORE", "t2"."OWNERDISPLAYNAME", "t2"."COMMENTCOUNT", "t2"."BADGECOUNT", "t2"."LASTHISTORYUPDATE", CASE WHEN "t4"."VOTECOUNT" IS NOT NULL THEN CAST("t4"."VOTECOUNT" AS BIGINT) ELSE 0 END AS "TOTALVOTES", CASE WHEN "t4"."UPVOTES" IS NOT NULL THEN CAST("t4"."UPVOTES" AS INTEGER) ELSE 0 END AS "UPVOTES", CASE WHEN "t4"."DOWNVOTES" IS NOT NULL THEN CAST("t4"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "DOWNVOTES"
FROM (SELECT ANY_VALUE("s1"."Id") AS "POSTID", "s1"."Title" AS "TITLE", "s1"."CreationDate" AS "CREATIONDATE", "s1"."ViewCount" AS "VIEWCOUNT", "s1"."Score" AS "SCORE", ANY_VALUE("s1"."DisplayName") AS "OWNERDISPLAYNAME", COUNT("s1"."Id1") AS "COMMENTCOUNT", COUNT("s1"."Id2") AS "BADGECOUNT", MAX("PostHistory"."CreationDate") AS "LASTHISTORYUPDATE"
FROM "s1"
LEFT JOIN "STACK"."PostHistory" ON "s1"."Id" = "PostHistory"."PostId"
GROUP BY "s1"."Id", "s1"."CreationDate", "s1"."Score", "s1"."ViewCount", "s1"."Title", "s1"."DisplayName") AS "t2"
LEFT JOIN (SELECT "Votes"."PostId" AS "POSTID", COUNT(*) AS "VOTECOUNT", SUM(CASE WHEN "VoteTypes"."Name" = 'UpMod' THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN "VoteTypes"."Name" = 'DownMod' THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."VoteTypes"
INNER JOIN "STACK"."Votes" ON "VoteTypes"."Id" = "Votes"."VoteTypeId"
GROUP BY "Votes"."PostId") AS "t4" ON "t2"."POSTID" = "t4"."POSTID"
ORDER BY "t2"."CREATIONDATE" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t6"