SELECT COALESCE("t"."POSTID", "t"."POSTID") AS "POSTID", "t"."TITLE", "t"."CREATIONDATE", "t"."SCORE", "t"."VIEWCOUNT", "t"."COMMENTCOUNT", "t"."ACCEPTEDANSWERID", "t"."OWNERDISPLAYNAME", "t"."OWNERUSERID", "t3"."USERID", "t3"."DISPLAYNAME", "t3"."POSTCOUNT", "t3"."QUESTIONCOUNT", "t3"."ANSWERCOUNT", "t3"."UPVOTES", "t3"."DOWNVOTES"
FROM (SELECT "Posts"."Id" AS "POSTID", "Posts"."Title" AS "TITLE", "Posts"."CreationDate" AS "CREATIONDATE", "Posts"."Score" AS "SCORE", "Posts"."ViewCount" AS "VIEWCOUNT", "Posts"."CommentCount" AS "COMMENTCOUNT", "Posts"."AcceptedAnswerId" AS "ACCEPTEDANSWERID", CASE WHEN "Users"."DisplayName" IS NOT NULL THEN CAST("Users"."DisplayName" AS VARCHAR CHARACTER SET "ISO-8859-1") ELSE 'Community' END AS "OWNERDISPLAYNAME", "Posts"."OwnerUserId" AS "OWNERUSERID"
FROM "STACK"."Posts"
LEFT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id") AS "t"
RIGHT JOIN (SELECT ANY_VALUE("t0"."Id") AS "USERID", "t0"."DisplayName" AS "DISPLAYNAME", COUNT(DISTINCT "t0"."Id0") AS "POSTCOUNT", SUM(CASE WHEN "PostTypes"."Name" = 'Question' THEN 1 ELSE 0 END) AS "QUESTIONCOUNT", SUM(CASE WHEN "PostTypes"."Name" = 'Answer' THEN 1 ELSE 0 END) AS "ANSWERCOUNT", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."Votes"
RIGHT JOIN ((SELECT "Users0"."Id", "Users0"."Reputation", "Users0"."CreationDate", "Users0"."DisplayName", "Users0"."LastAccessDate", "Users0"."WebsiteUrl", "Users0"."Location", "Users0"."AboutMe", "Users0"."Views", "Users0"."UpVotes", "Users0"."DownVotes", "Users0"."ProfileImageUrl", "Users0"."AccountId", "Posts0"."Id" AS "Id0", "Posts0"."PostTypeId", "Posts0"."AcceptedAnswerId", "Posts0"."ParentId", "Posts0"."CreationDate" AS "CreationDate0", "Posts0"."Score", "Posts0"."ViewCount", "Posts0"."Body", "Posts0"."OwnerUserId", "Posts0"."OwnerDisplayName", "Posts0"."LastEditorUserId", "Posts0"."LastEditorDisplayName", "Posts0"."LastEditDate", "Posts0"."LastActivityDate", "Posts0"."Title", "Posts0"."Tags", "Posts0"."AnswerCount", "Posts0"."CommentCount", "Posts0"."FavoriteCount", "Posts0"."ClosedDate", "Posts0"."CommunityOwnedDate", "Posts0"."ContentLicense"
FROM "STACK"."Posts" AS "Posts0"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Posts0"."OwnerUserId" = "Users0"."Id") AS "t0" LEFT JOIN "STACK"."PostTypes" ON "t0"."PostTypeId" = "PostTypes"."Id") ON "Votes"."PostId" = "t0"."Id0"
GROUP BY "t0"."Id", "t0"."DisplayName") AS "t3" ON "t"."OWNERUSERID" = "t3"."USERID"