SELECT COALESCE("PostTypes"."Name", "PostTypes"."Name") AS "Name", ANY_VALUE("PostTypes"."Name") AS "POSTTYPE", COUNT(*) AS "TOTALPOSTS", AVG("t3"."COMMENTCOUNT") AS "AVGCOMMENTS", AVG("t3"."VOTECOUNT") AS "AVGVOTES", AVG("t3"."AVGUSERREPUTATION") AS "AVGOWNERREPUTATION"
FROM "STACK"."PostTypes"
INNER JOIN (SELECT ANY_VALUE("t0"."Id") AS "POSTID", "t0"."PostTypeId" AS "POSTTYPEID", "t0"."CreationDate" AS "CREATIONDATE", COUNT(CASE WHEN "t0"."Id0" IS NOT NULL THEN 1 ELSE NULL END) AS "COMMENTCOUNT", COUNT("t0"."Id1") AS "VOTECOUNT", AVG("Users"."Reputation") AS "AVGUSERREPUTATION"
FROM (SELECT "t"."Id", "t"."PostTypeId", "t"."AcceptedAnswerId", "t"."ParentId", "t"."CreationDate", "t"."Score", "t"."ViewCount", "t"."Body", "t"."OwnerUserId", "t"."OwnerDisplayName", "t"."LastEditorUserId", "t"."LastEditorDisplayName", "t"."LastEditDate", "t"."LastActivityDate", "t"."Title", "t"."Tags", "t"."AnswerCount", "t"."CommentCount", "t"."FavoriteCount", "t"."ClosedDate", "t"."CommunityOwnedDate", "t"."ContentLicense", "t"."Id0", "t"."PostId", "t"."Score0", "t"."Text", "t"."CreationDate0", "t"."UserDisplayName", "t"."UserId", "t"."ContentLicense0", "Votes"."Id" AS "Id1", "Votes"."PostId" AS "PostId0", "Votes"."VoteTypeId", "Votes"."UserId" AS "UserId0", "Votes"."CreationDate" AS "CreationDate1", "Votes"."BountyAmount"
FROM "STACK"."Votes"
RIGHT JOIN (SELECT "Posts"."Id", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate", "Posts"."Score", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense", "Comments"."Id" AS "Id0", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate0", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN "STACK"."Posts" ON "Comments"."PostId" = "Posts"."Id") AS "t" ON "Votes"."PostId" = "t"."Id") AS "t0"
LEFT JOIN "STACK"."Users" ON "t0"."OwnerUserId" = "Users"."Id"
GROUP BY "t0"."Id", "t0"."PostTypeId", "t0"."CreationDate") AS "t3" ON "PostTypes"."Id" = "t3"."POSTTYPEID"
GROUP BY "PostTypes"."Name"